<?phpinclude '../bin/dbinfo.inc.php';//CONNECT TO MYSQL$connection = @mysql_connect($mysqladd,$mysqluser,$mysqlpass) or die(mysql_error());$database = @mysql_select_db($databasename,$connection) or die(mysql_error());$instance_id = $_GET['instance_id'];$getOrderSQL = "SELECT * FROM tcps_orders WHERE instance_id = '$instance_id'";$getOrderResult = mysql_query($getOrderSQL,$connection);while($row = mysql_fetch_array($getOrderResult,MYSQL_ASSOC)){	$orderData = $row;}$date = $orderData['activation_date'];$time = $orderData['timeslot'];if(strlen($time) == 3){ $hour = substr($time,0,1); }else{ $hour = substr($time,0,2); }$day = substr($date,-2);if(substr($day,0,1) == 0){$day = substr($day,-1); }$month =  substr($date,5,2);$year = substr($date,0,4);$mktimeDate = mktime($hour,30,0,$month,$day,$year);$timeDisplay = date("g:iA",$mktimeDate);$dateDisplay = date("l, F jS, Y",$mktimeDate);?><html><head><!--[if IE]><link rel="stylesheet" type="text/css" href="../style/tracker_style_ie.css" /><![endif]--><!--[if !IE]><!--><link rel="stylesheet" type="text/css" href="../style/tracker_style.css" /><!--<![endif]--></head><style>body{	background-color:lightgray;	font-size:90%;}p{	font-size:90%;}table{	position:relative;	margin-left:auto;	margin-right:auto;}td{	font-size:90%;}.leftCol{		text-align:right;}.rightCol{}</style><script type="text/javascript">    function show_confirm(){	var Missing = "";	var Msg = "";	//check for NULL values	if(document.forms[0].removed_by.value==""){		Missing = Missing + "Your Name\n";		}				//NULL values alert		if(Missing != ""){			Missing = "No values provided for the following field(s):\n\n" + Missing;		alert(Missing)		return false;	}		}</script><body onLoad="document.forms.slotReservationData.reserved_by.focus()"><center><?phpecho "<h4><u>Remove TCPS Time Slot</u></h4><h3>$timeDisplay* on $dateDisplay</h3><p>This slot is currentl