<?php$time = $_GET['slot'];$timeslot = date("Gi",$time);$date = date("Y-m-d",$time);$timeDisplay = date("g:i A",$time);$dateDisplay = date("D, M jS, Y",$time);?><html><script src="//ajax.googleapis.com/ajax/libs/jquery/1.9.1/jquery.min.js"></script><script>$(document).ready(function(){		$.ajaxSetup({ cache:false });		$("#slotSubmit").click(function(){		var acDate = document.getElementById("acDate").value;		var formData = $('#slotReservationData').serialize();		$.get('slotSubmit.php',formData,function(data,status){});		$("#reserveSlotForm").css("display","none");		alert(acDate);		setTimeout(function(){			$("#dateDetail").load("dateDetail.php?date=" + acDate);		},300);					});				$("#slotCancel").click(function(){		$("#reserveSlotForm").css("display","none");	});				});</script><body><?phpecho "Enter the following information to reserve the slot for:<br />$timeDisplay on $dateDisplay";?><form name="slotReservationData" id="slotReservationData"><p>Your Name: <input type="text" name="reserved_by" /></p><p>Acct Num: <input type="text" name="acct_num" /></p><p>Acct Name: <input type="text" name="acct_name" /></p><p>Order Num: <input type="text" name="order_num" /></p><input type="hidden" name="timeslot" value="<?php echo $time; ?>" /><input type="hidden" name="acDate" id="acDate" value="<?php echo $date; ?>" /></form><p><input id="slotSubmit" type="submit" name="submit" value="Submit" /> <button id="slotCancel" >Cancel</button></p></body></html>