Missing + "Your Name\n";		}		if(document.forms[0].acct_num.value==""){		Missing = Missing + "Account Number\n";		}	if(document.forms[0].acct_name.value==""){		Missing = Missing + "Account Name\n";		}	if(document.forms[0].order_num.value==""){		Missing = Missing + "Order Number\n";		}		if(document.forms[0].order_type.value==""){		Missing = Missing + "Order Type\n";		}		if(offnet==""){		Missing = Missing + "Off-Net\n";		}					//NULL values alert		if(Missing != ""){			Missing = "No values provided for the following field(s):\n\n" + Missing;		alert(Missing)		return false;	}			var acct_num = document.forms[0].acct_num.value;			//check order number format	var acct_num_pattern = /^\d{4,6}$/;	if(acct_num.match(acct_num_pattern) == null && acct_num != ""){		alert("ERROR! - Account number is invalid. \n\n Please double-check the account number.")		return false;	}			var order_num = document.forms[0].order_num.value;			//check order number format	var order_num_pattern = /^1-\w{6,7}$/;	if(order_num.match(order_num_pattern) == null && order_num != ""){		alert("ERROR! - Order number is invalid. \n\n Please double-check the order number.")		return false;	}}</script><body onLoad="document.forms.slotReservationData.reserved_by.focus()"><center><?phpecho "<h4><u>TCPS Time Slot Reservation</u></h4><h3>$timeDisplay* on $dateDisplay</h3><p>Please enter the following information to reserve slot:</p>";?><form action="reserveSlotSubmit.php" method="POST" onsubmit="return show_confirm()" name="slotReservationData" id="slotReservationData"><table><tr><td class="leftCol">Your Name</td><td><input type="text" class="text" name="reserved_by" /></td></tr><tr><td class="leftCol">Account Number</td><td><input type="text" class="text" name="acct_num" /></td></tr><tr><td class="leftCol">Account Name</td><td><input type="text" class="text" name="acct_name" /></td></tr><tr><td class="leftCol">Order Number</td><td><input type="text" class="text" name="order_num" /></td></tr><tr><td class="leftCol">Order Type</td><td><select name="order_type">	<option value=""></option>	<option value="New">New</option>	<option value="Conversion">Conversion</option></select></td></tr><tr><td class="leftCol">Off-Net?</td><td>Yes<input type="radio" name="offnet" value="1"> No<input type="radio" name="offnet" value="0"></td></tr></table><input type="hidden" name="timeslot" value="<?php echo $time; ?>" /><input type="hidden" name="acDate" id="acDate" value="<?php echo $date; ?>" /><br /><input id="slotSubmit" type="submit" name="submit" value="Submit" /><input type="reset" name="reset" value="Reset" /><button type="button" id="slotCancel" onClick="window.close();">Cancel</button></form></center><br /><p style="text-align:right;margin-right:10px;">* Eastern Time</p></body></html>